mapper.select:
select AnimalNumber,PigAge,HealthCondition,LifeCondition from 2020rg_group13_sowrecordlnfo


        AnimalNumber=#{AnimalNumber}


        PigAge=#{PigAge}

    and IfBreedSelectObserved='是'
    and IfBreedSelect='否'

ORDER BY AnimalNumber asc
limit #{currentPage},#{pageSize}

select count(AnimalNumber) from 2020rg_group13_sowrecordlnfo


        AnimalNumber=#{AnimalNumber}


        PigAge=#{PigAge}

    and IfBreedSelectObserved='是'
    and IfBreedSelect='否'

ORDER BY  AnimalNumber asc

mapper.update:
update 2020rg_group13_sowrecordlnfo
IfBreedSelectObserved='否', IfBreedSelect='是'
where AnimalNumber = #{AnimalNumber}
